import { Component, OnDestroy, OnInit } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss']
})
export class HomeComponent implements OnInit, OnDestroy  {
  // if the img intersects with the viewport, 
  // and the existing width is 80%:  I want to set the height to be 80% and width auto. 
  // if the existing width is something else: I want to sent the width to be 80% and height auto 

  targetElem = document.querySelector("#image")!;
  observer : IntersectionObserver|null = null;

  constructor() {}

   ngOnInit() {
    let options = {
      root: document.querySelector(".background"), //defaults to Viewport 
      rootMargin: '0px',
      threshold: 1.0
    }

    this.observer = new IntersectionObserver(this.callback, options); 
    this.observer.observe(this.targetElem);
   }

   ngOnDestroy() {
     this.observer!.disconnect();
   }

  callback() {
    if (this.targetElem.getAttribute('width') === "80%") {
     this.targetElem.setAttribute('height', "80%");
     this.targetElem.setAttribute('width', 'auto');
    } else {
      this.targetElem.setAttribute('height', "auto");
      this.targetElem.setAttribute('width', '80%');
    }
  }  
}
